package Testss;

public class PostAndPreFix {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=2;
		System.out.println(num);
		System.out.println(num++);
		System.out.println(num);
		
		num =2;
		System.out.println(num);
		System.out.println(++num);
		System.out.println(num);

	}

}
